mapper.select:
select c1,location,year, sum(nvl(c2,0)) c2, sum(nvl(c3,0)) c3, sum(nvl(c4,0)) c4, avg(nvl(c5,0)) c5 from ACT_CNNSDYCN
location = #{location}
group by c1,location,year order by c0

mapper.update:
update ACT_CNNSDYCN set c1 = #{c1},
c2 = #{c2},c3 = #{c3},c4 = #{c4},c5 = #{c5}
where location = #{location}
and year = #{year}

mapper.delete:
delete from ACT_CNNSDYCN


		and location = #{location}


		and year = #{year}
