mapper.select:
select c1,location,year, sum(nvl(c2,0)) c2, avg(nvl(c3,0)) c3, sum(nvl(c4,0)) c4, avg(nvl(c5,0)) c5, sum(nvl(c6,0)) c6, avg(nvl(c7,0)) c7, sum(nvl(c8,0)) c8, avg(nvl(c9,0)) c9, sum(nvl(c10,0)) c10, avg(nvl(c11,0)) c11, sum(nvl(c12,0)) c12, avg(nvl(c13,0)) c13, sum(nvl(c14,0)) c14, avg(nvl(c15,0)) c15, sum(nvl(c16,0)) c16, avg(nvl(c17,0)) c17 from ACT_CNNSDYJQ
location = #{location}
group by c1,location,year order by c0

mapper.delete:
delete from ACT_CNNSDYJQ


		and location = #{location}


		and year = #{year}
