mapper.select:
select c1,year, sum(nvl(c2,0)) c2, sum(nvl(c3,0)) c3, sum(nvl(c4,0)) c4, sum(nvl(c5,0)) c5, sum(nvl(c6,0)) c6, sum(nvl(c7,0)) c7, sum(nvl(c8,0)) c8 from ACT_CNSTRNXQ
location = #{location}
group by c1,year order by c0

mapper.update:
update ACT_CNSTRNXQ set
c2 = #{c2},c3 = #{c3},c4 = #{c4},c5 = #{c5},c6 = #{c6},c7 = #{c7},c8 = #{c8}
where c1 = #{c1} and location = #{location} and year = #{year}

mapper.delete:
delete from ACT_CNSTRNXQ 


		c1 = #{c1}


		and location = #{location}


		and year = #{year}
