mapper.select:
select year, sum(nvl(c2,0)) c2, sum(nvl(c3,0)) c3, avg(nvl(c4,0)) c4, sum(nvl(c5,0)) c5, sum(nvl(c6,0)) c6, avg(nvl(c7,0)) c7, sum(nvl(c8,0)) c8, sum(nvl(c9,0)) c9, sum(nvl(c10,0)) c10, sum(nvl(c11,0)) c11, sum(nvl(c12,0)) c12, sum(nvl(c13,0)) c13, sum(nvl(c14,0)) c14, sum(nvl(c15,0)) c15, sum(nvl(c16,0)) c16, sum(nvl(c17,0)) c17 from ACT_CWSJSRGZ
location = #{location}
group by year order by year

mapper.delete:
delete from ACT_CWSJSRGZ


		year = #{year}


		and location = #{location}
